function fourier(deltaT = 0.025,suppression = false,iterationPic = 0)f = @(t) cos(2*pi*t) + 0.9*cos(2*pi*t*10);getId = @(deltaT,x,iteration) iteration*deltaT*length(x);#Variables globalesx = [0:deltaT:(10-deltaT)];y = f(x);fmax = 1/deltaT;#Transformée de FourierXcomplex = fft(y);echantillons = length(Xcomplex);deltaF = fmax / echantillons;Y1 = abs(Xcomplex) / echantillons;F1 = [0:deltaF:(fmax-deltaF)];Y3 = ifft(Xcomplex);#Suppression d'un picif (suppression)  bi = iterationPic; #Iteration de bi, c'est-à-dire la valeur de i  id1 = getId(deltaT,x,bi);  id2 = length(x) - id1;  Y1(id1+1) = 0; #supprime le pic de l'itération  bi  Y1(id2+1) = 0; #La valeur est supprimée pour le graphe fréquenciel  Xcomplex(id1+1) = 0; #La valeur est supprimée dans les points de la transformée de Fourier  Xcomplex(id2+1) = 0;endif;#Transformée de Fourier inverseY2 = ifft(Xcomplex);nbGraphique = 3;subplot(nbGraphique,1,1);plot(x,y);info = sprintf("f(t) = cos(2*pi*t) + 0.9*cos(2*pi*t*10)\n delta t = %d",deltaT); title(info);xlabel("Temps [s]");ylabel("Amplitude [u]");grid;subplot(nbGraphique,1,2);plot(F1,Y1,"-r");title("Transformée de Fourier");xlabel("Fréquence [f]");ylabel("Amplitude [u]");grid;if (suppression)  subplot(nbGraphique,1,3);  plot(x,Y3,"-b",x,Y2,"-g");  title("Transformée de Fourier inverse");  xlabel("Temps [s]");  ylabel("Amplitude [u]");else  subplot(nbGraphique,1,3);  plot(x,Y2,"-g");  title("Transformée de Fourier inverse");  xlabel("Temps [s]");  ylabel("Amplitude [u]");  grid;endif;endfunction